    <button onclick="addNumbers()">Add Numbers</button>

    <script>
        function addNumbers() {
            // Get the values from the input fields
            const num1 = document.getElementById('num1').value;
            const num2 = document.getElementById('num2').value;

            // Function to validate and convert input
            function validateAndConvert(num) {
                const number = parseInt(num, 10);
                if (isNaN(number)) {
                    alert('Please enter a valid number!');
                    return null;
                }
                return number;
            }

            // Convert both inputs and add them
            const num1Valid = validateAndConvert(num1);
            const num2Valid = validateAndConvert(num2);

            if (isNaN(num1Valid) || isNaN(num2Valid)) {
                alert('Please enter valid numbers in both input fields!');
            } else {
                const sum = num1Valid + num2Valid;
                alert('The sum of the numbers is ' + sum + '.');
            }
        }
    </script>
</body>
