#include <vector>

// Function to calculate the nth number in the sequence
int sequence(int n) {
    if (n <= 2) {
        return -1; // Special cases: first two numbers are -1
    } else if (n == 3) {
        return 0; // Third number: sum of first two numbers
    } else {
        // Initialize the vector with the first three numbers
        std::vector<int> prev(4);
        prev[1] = -1;
        prev[2] = -1;
        prev[3] = 0;

        // Calculate each number up to the nth number
        for (int i = 4; i <= n; ++i) {
            prev[i] = prev[i-1] + prev[i-2] + prev[i-3];
        }

        return prev[n];
    }
}

