#include <iostream>

// Function to calculate the nth Tribonacci number using recursion
int tribonacci(int n, int* previousNumbers = NULL, int size = 3) {
    int temp[n + 1];

    if (n < size) {
        if (n < 0) {
            std::cout << "Invalid input. Please enter a non-negative integer." << std::endl;
            return 0; // Terminate the program with an error code
        }
        temp[n] = (n > 0)? tribonacci(n - 1, previousNumbers, size) + (n > 1)? tribonacci(n - 2, previousNumbers, size) : previousNumbers[0] : previousNumbers[0];
        for (int i = 0; i <= n; ++i) {
            previousNumbers[i] = temp[i];
        }
    }

    return temp[n];
}

// Function to reset the Tribonacci sequence and calculate the first 3 numbers
int* initializeTribonacci(int size = 3) {
    static int previousNumbers[size];

    previousNumbers[0] = 0;
    previousNumbers[1] = -1;
    previousNumbers[2] = -1;

    return previousNumbers;
}

// Main function
int main() {
    int n; // Input number for which we want to find the Tribonacci number
    int* previousNumbers = initializeTribonacci();

    // Prompt user for input
    std::cout << "Enter the value of n: ";
    std::cin >> n;

    // Calculate and display the nth Tribonacci number
    std::cout << "The " << n << "th Tribonacci number is: " << tribonacci(n, previousNumbers) << std::endl;

    return 0; // Success
}
