            { joke: "Why don't scientists trust atoms? Because they make up everything!", id: 1 },
            { joke: "Why don't eggs tell jokes? They'd crack each other up!", id: 2 },
            { joke: "Why did the scarecrow win an award? Because he was outstanding in his field!", id: 3 },
            { joke: "What do you call a fake noodle? An impasta!", id: 4 }
        ];

        jokeBtn.addEventListener("click", displayJoke);

        function displayJoke() {
            let randomId = Math.floor(Math.random() * jokes.length) + 1;
            jokes.forEach(joke => {
                if (joke.id === randomId) {
                    jokeDisplay.textContent = joke.joke;
                }
            });
        }
    </script>
</body>
</html>
