class Graph:
    def __init__(self):
        self.adjacency_list = {}

    def add_edge(self, node1, node2):
        """Add an edge between two nodes in the graph."""
        if node1 not in self.adjacency_list:
            self.adjacency_list[node1] = []
        if node2 not in self.adjacency_list:
            self.adjacency_list[node2] = []
        self.adjacency_list[node1].append(node2)
        self.adjacency_list[node2].append(node1)

    def shortest_path(self, node1, node2):
        """Find the shortest path from node1 to node2 in the graph."""
        visited = set()
        queue = [(node1, [node1])]
        while queue:
            current_node, path = queue.pop(0)
            if current_node == node2:
                return path
            for neighbor in self.adjacency_list.get(current_node, []):
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, path + [neighbor]))
        return None

    def highest_common_ancestor(self, node1, node2):
        """Find the highest common ancestor of two nodes in the graph."""
        path1 = self.shortest_path(node1, node2)
        if path1 is None:
            return None
        i = 0
        while i < len(path1) - 1 and path1[i] == path1[i + 1]:
            i += 1
        return path1[i] if i > 0 else None


