def top2_elements(nums):
    """
    Finds the top-2 most occurring elements in a given integer array.

    Args:
        nums (list): A list of integers.

    Returns:
        list: A list containing the top-2 most occurring elements, or an empty list if no such elements exist.

    Raises:
        ValueError: If the input list is empty.
    """
    if not nums:
        raise ValueError("Input list cannot be empty")

    # Phase 1: Find the majority element using Boyer-Moore Voting Algorithm
    candidate = nums[0]
    count = 1

    for num in nums[1:]:
        if count == 0:
            candidate = num
            count = 1
        elif candidate == num:
            count += 1
        else:
            count -= 1

    # Phase 2: Find the second majority element
    candidate_2 = None
    count_2 = 0

    # Iterate through the array again to find the second majority element
    for num in nums:
        if num == candidate:
            count += 1
        elif num == candidate_2:
            count_2 += 1
        elif count_2 == 0:
            candidate_2 = num
            count_2 = 1
        elif num!= candidate:
            count -= 1
            count_2 -= 1

    # Verify that the candidates are actually the majority elements
    result = []

    if nums.count(candidate) > len(nums) / 2:
        result.append(candidate)

    if candidate_2 and nums.count(candidate_2) > len(nums) / 2:
        result.append(candidate_2)

    if len(result) == 1:
        # If only one majority element exists, add the element with the second highest count
        counts = {}
        for num in nums:
            counts[num] = counts.get(num, 0) + 1

        second_highest_candidate = max([k for k, v in counts.items() if k!= result[0] and v > counts[result[0]]])

        if second_highest_candidate:
            result.append(second_highest_candidate)

    return sorted(result)
