/**
 * Calculates the nth number in the sequence, where each number is the sum of the three preceding ones.
 *
 * @param n The position of the number to calculate.
 * @return The nth number in the sequence.
 */
long long sequence(int n) {
    if (n == 1) {
        // Initial value for the sequence
        return 0;
    } else if (n == 2) {
        // Second value in the sequence
        return -1;
    } else if (n == 3) {
        // Third value in the sequence
        return -1;
    } else {
        // Recursive case: F(n) = F(n-1) + F(n-2) + F(n-3)
        return sequence(n - 1) + sequence(n - 2) + sequence(n - 3);
    }
}

