class Solution:
    def lowestCommonAncestor(self, root: TreeNode, p: TreeNode, q: TreeNode) -> TreeNode:
        stack = [(root, False)]
        while stack:
            node, visited = stack.pop()
            if node:
                if node == p or node == q:
                    return node
                for child in [node.left, node.right]:
                    stack.append((child, False))
                visited = True
            if visited:
                if stack:
                    return stack[-1][0]
        return None
