    ```python
class Solution:
    def lowestCommonAncestor(self, root: List[TreeNode], p: TreeNode, q: TreeNode) -> TreeNode:
        for r in root:
            lca = self.lowestCommonAncestor2(r, p, q)
            if lca:
                return lca
        return None

    def lowestCommonAncestor2(self, root: TreeNode, p: TreeNode, q: TreeNode) -> TreeNode:
        if not root or root == p or root == q:
            return root
        left_lca = self.lowestCommonAncestor2(root.left, p, q)
        right_lca = self.lowestCommonAncestor2(root.right, p, q)
        if left_lca and right_lca:
            return root
        return left_lca or right_lca
