import re
import json

def extract_variables(equations):
    variables = {}
    for equation in equations:
        variables_dict = {}
        for term in equation.split():
            if term.isalpha() or term.isdigit():
                variables_dict[term] = None
            else:
                for variable in re.findall(r'(\w+|[^\w\s,']+)(?=[^\w\s,']+|$)', term):
                    if variable in variables_dict:
                        variables_dict[variable] += '*'
                    else:
                        variables_dict[variable] = '*'
    for term in equation.split():
        if term.isalpha() or term.isdigit():
            variables_dict[term] = None
    unique_variables = list(variables_dict.keys())
    return unique_variables

def convert_to_json(equations):
    variables_str = json.dumps(extract_variables(equations), indent=2)
    variables_json = json.loads(variables_str)
    return json.dumps(variables_json, indent=2)

