import json
import re

def extract_variables(equation):
    variables = set()
    for term in equation.split("+"):
        term = term.strip()
        if "x" in term:
            variables.add("x")
        elif "y" in term:
            variables.add("y")
        else:
            for factor in term:
                if factor.isdigit():
                    variables.add(f"c{factor}")
                else:
                    variables.add(factor)
    return list(variables)

