import re
import json

def extract_variable_names(equation):
    equation = re.sub(r'[^\w\d_]', '', equation)  # remove all non-alphanumeric characters
    equation = re.sub(r'\s+', '_', equation)  # replace spaces with underscores
    equation = re.sub(r'^_', '', equation)  # remove leading underscores
    variable_names = set()
    for term in equation.split('+'):
        for factor in term.split('*'):
            if '^' in factor:
                exponents = factor.split('^')
                variable_names.update(exponents)
            elif '/' in factor:
                numerator, denominator = factor.split('/')
                if denominator.isdigit():
                    denominator = int(denominator)
                else:
                    denominator = set(denominator.split('*'))
                for name in numerator.split():
                    variable_names.update(name.split('*')[0].split('^'))
                for name in denominator:
                    variable_names.update(name.split('*'))
            else:
                variable_names.update(factor.split('^'))
    return list(variable_names)

