import csv

# Define a function to extract the highest and lowest closing prices for a given month
def extract_monthly_data(last_day, prices):
    monthly_data = {}
    for i in range(len(prices)):
        date = prices[i][0]
        if date.month != last_day.month:
            # Start a new month
            monthly_data[date.strftime('%Y-%m-01')] = {'high': prices[-1][3], 'low': prices[-1][2]}
