@dataclass
class Node:
    id: str
    name: str
    children: List[str]

# Define a data class to represent the hierarchy
@dataclass
class Hierarchy:
    nodes: Dict[str, Node]

# Function to load the hierarchy from a file
def load_hierarchy(filename: str) -> Hierarchy:
    """Load a hierarchy from a YAML file."""

    try:
        with open(filename, 'r') as file:
            # Load the YAML data into a dictionary
            data = yaml.safe_load(file)
            # Create the hierarchy dictionary
            hierarchy = Hierarchy({node['id']: Node(node['id'], node['name'], node['children']) for node in data})
            return hierarchy
    except FileNotFoundError:
        print(f"Error: The file '{filename}' was not found.")
        return None
    except yaml.YAMLError:
        print(f"Error: The file '{filename}' contains invalid YAML.")
        return None

# Function to print the hierarchy in a human-readable format
def print_hierarchy(hierarchy: Hierarchy):
    """Print the hierarchy in a human-readable format."""

    for node in hierarchy.nodes.values():
        print(f"Node: {node.id} - {node.name} - Children: {', '.join(node.children)}")
        for child in node.children:
            child_node = hierarchy.nodes[child]
            print(f"  +-- {child_node.id} - {child_node.name}")

# Function to build the hierarchy string
