        this.add(args[0], args[1]);
        break;
      case Opcode.SUB:
        this.subtract(args[0], args[1]);
        break;
      case Opcode.MUL:
        this.multiply(args[0], args[1]);
        break;
      case Opcode.DIV:
        this.divide(args[0], args[1]);
        break;
      case Opcode.LOAD_CONSTANT:
        this.pushStack(args[0]);
        break;
      case Opcode.LOAD_CONST_INT:
        this.pushStack(args[0]);
        break;
      default:
        throw new Error(`Unknown opcode: ${op}`);
    }
  }

