function countConnections(operations) {
  let transferCounts = new Map(); // Map to store the count of intervals that transfer from one index to another

  operations.sort((a, b) => a[0] - b[0]); // Sort the operations by their start time

  let transferMap = new Map(); // Map to store the intervals that transfer values (key: start index, value: transfer value)

  for (let [start, end, value] of operations) {
    if (!(start in transferMap)) {
      transferMap.set(start, value);
    } else {
      let transferValue = transferMap.get(start);
      transferMap.set(start, transferValue + value);
      transferCounts.set(start, (transferCounts.get(start) || 0) + 1); // Update the count of transferring intervals
    }
  }

  // Implementing the recursive check
  function checkForRecursion(index) {
    while (true) {
      // Recursive check logic would go here
