      if (nextIndex!== index) {
        index = nextIndex;
      } else {
        break;
      }
    }
  }

  // You would implement the `getTransferIndex()` function to return the next index based on the current index
  // For the sake of simplicity, let's assume it returns the next index in the transfer map
  function getTransferIndex(index) {
    let nextIndex = index + 1;
    if (transferMap.has(nextIndex)) {
      return nextIndex;
    } else {
      return null;
    }
  }

  // To implement check if we need to recurse
