        // Use a temporary variable to get the element we want to move
        let element_to_move = vector.remove(index_to_move as usize);

        let new_position = (index_to_move as i32 + new_position_diff) as usize;

        // Insert the element at the new position and extend the rest of the vector with the tail
        vector.splice(..new_position, [element_to_move]);
    } else {
        let start = if (index_to_move as i32 + new_position_diff) < 0 {
            0
        } else {
            (index_to_move as i32 + new_position_diff) as usize
        };

        let end = if (index_to_move as i32 + new_position_diff) >= vector.len() as i32 {
            vector.len()
