        let original_position = if (index_to_move as i32 + new_position_diff) < 0 {
            0
        } else {
            (index_to_move as i32 + new_position_diff) as usize
        };

        vector.insert(original_position, elements.collect::Vec<_>().remove(0));
    }
}

