// pages/api/auth.js
import jwt from 'jsonwebtoken';
import bcrypt from 'bcryptjs';
import { NextApiRequest, NextApiResponse } from 'next';

const jwtSecret = process.env.JWT_SECRET;

const register = async (req: NextApiRequest, res: NextApiResponse) => {
    const { name, email, password } = req.body;
    const hashedPassword = await bcrypt.hash(password, 8);
    const token = jwt.sign({ id: 1, name, email }, jwtSecret, { expiresIn: '1h' });
    res.status(200).json({ token });
};

const login = async (req: NextApiRequest, res: NextApiResponse) => {
    const { email, password } = req.body;
    const user = { email: 'user@example.com', password: 'password123' }; // Replace with data from a database
    const isValidPassword = await bcrypt.compare(password, user.password);
    if (!isValidPassword) {
        return res.status(401).json({ error: 'Invalid password' });
    }
    const token = jwt.sign({ id: 1, email }, jwtSecret, { expiresIn: '1h' });
    res.status(200).json({ token });
};

export default async function handle(req: NextApiRequest, res: NextApiResponse) {
    const { endpoint } = req.query;
    if (endpoint ==='register') {
        register(req, res);
    } else if (endpoint === 'login') {
        login(req, res);
    } else {
        res.status(404).json({ error: 'Not found' });
    }
}
