@Component({
  selector: 'app-example',
  template: '<button (click)="downloadImage()">Download Image</button>'
})
export class ExampleComponent {
  url = 'your-image-url';

  constructor(private http: HttpClient) { }

  downloadImage() {
    this.http.get(this.url, { responseType: 'blob' as 'json' })
     .subscribe(response => {
        const blob = new Blob([response], { type: 'image/jpeg' });
        this.saveBlob(blob, 'image.jpg');
      });
  }

  saveBlob(blob: Blob, filename: string) {
    const disposalUrl = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = disposalUrl;
    link.download = filename;
    link.click();
    URL.revokeObjectURL(disposalUrl);
  }
}
