        // Create a new JSONObject
        JSONObject jsonObject = new JSONObject();
        jsonObject.put("key", "value");

        // Convert JSONObject to ByteArrayContent
        String jsonString = jsonObject.toString();
        byte[] bytes = jsonString.getBytes("utf-8");
        HttpContent httpContent = new ByteArrayContent(bytes);

        // Specify the request URL and HTTP method
        GenericUrl url = new GenericUrl("https://example.com/api/endpoint");
        HttpRequest request = com.google.api.client.http.HttpRequest.newHttpRequest(url);
        request.setMethod("POST");
