const MAZE_WIDTH = 20;
const MAZE_HEIGHT = 20;
const WALL_COLOR = 'black';
const PATH_COLOR = 'white';

// Function to create a new maze
function createMaze(width, height) {
  const maze = [];
  const visited = {};

  // Initialize the maze with all walls
  for (let i = 0; i < height; i++) {
    maze[i] = [];
    for (let j = 0; j < width; j++) {
      maze[i][j] = '#'; // '#'
    }
  }

  // Choose a random cell as the starting point
  let startX = Math.floor(Math.random() * width);
  let startY = Math.floor(Math.random() * height);

  // Mark the starting cell as visited
  visited[`${startX},${startY}`] = true;

  // Loop until all cells are visited
  while (visited.length < width * height) {
    const currentX = Math.floor(Math.random() * width);
    const currentY = Math.floor(Math.random() * height);

    // If the current cell is not visited, mark it as visited
    if (!visited[`${currentX},${currentY}`]) {
