            [HttpTrigger(AuthorizationLevel.Function, "post", Route = null)] HttpRequestData req,
            ILogger logger,
            ExecutionContext context)
        {
            logger.LogInformation("C# HTTP trigger function processed a request.");

            // Get the queue client instance
            string serviceBusConnectionString = Environment.GetEnvironmentVariable("ServiceBusConnectionString");
            string queuePath = Environment.GetEnvironmentVariable("QueuePath");

            ServiceBusClient serviceBusClient = new ServiceBusClient(serviceBusConnectionString);
            ServiceBusSender sender = serviceBusClient.CreateSender(queuePath);

            string messageBody = "Hello, World!";
