                return os.path.basename(project_name_file)

        # If not found, move up to the parent directory and recurse
        parent_dir = os.path.join(os.path.dirname(current_dir), os.path.basename(current_dir))
        if os.path.exists(parent_dir) and parent_dir!= current_dir:
            return self.find_project_name(max_depth, parent_dir, depth + 1)
        else:
