import requests
import json

class LichessBotAPI:
    def __init__(self, api_key, bot_id):
        self.api_key = api_key
        self.bot_id = bot_id
        self.base_url = "https://lichess.org/api/bots"

    def get_bot_status(self):
        url = f"{self.base_url}/bots/{self.bot_id}/status"
        headers = {"Authorization": f"Bearer {self.api_key}"}
        response = requests.get(url, headers=headers)
        return response.json()

    def update_bot_status(self, status):
        url = f"{self.base_url}/bots/{self.bot_id}/status"
        headers = {"Authorization": f"Bearer {self.api_key}", "Content-Type": "application/json"}
        data = json.dumps({"status": status})
        response = requests.post(url, headers=headers, data=data)
        return response.json()

    def get_games(self):
        url = f"{self.base_url}/bots/{self.bot_id}/games"
        headers = {"Authorization": f"Bearer {self.api_key}"}
        response = requests.get(url, headers=headers)
        return response.json()

    def get_game(self, game_id):
        url = f"{self.base_url}/bots/{self.bot_id}/games/{game_id}"
        headers = {"Authorization": f"Bearer {self.api_key}"}
        response = requests.get(url, headers=headers)
        return response.json()

    def make_move(self, game_id, move):
        url = f"{self.base_url}/bots/{self.bot_id}/games/{game_id}/moves"
        headers = {"Authorization": f"Bearer {self.api_key}", "Content-Type": "application/json"}
        data = json.dumps({"move": move})
        response = requests.post(url, headers=headers, data=data)
