```csharp
public class ThumbOpacityController : MonoBehaviour
{
    public CanvasGroup thumbCanvasGroup;

    // When mouse is not hovering over the thumb (NOT the current thumb) set opacity to 0.65
    public float idleOpacity = 0.65f;

    // When cursor is hovering over the thumb, set opacity to 1 (fully opaque)
    public float hoveredOpacity = 1f;

    private bool isCurrentlyHovered = false;

    private void OnMouseOver()
    {
        isCurrentlyHovered = true;
        AnimateOpacity(hoveredOpacity);
    }

    private void OnMouseExit()
    {
        isCurrentlyHovered = false;
        AnimateOpacity(idleOpacity);
    }

    private void Update()
    {
        if(input.IsMouseOverGameObjects(thumbCanvasGroup))
        {
            isCurrentlyHovered = true;
            AnimateOpacity(hoveredOpacity);
        }
    }

    private void AnimateOpacity(float targetOpacity)
    {
        StopAllCoroutines();
        StartCoroutine(AnimateOpacityCo(targetOpacity));
    }

    private IEnumerator AnimateOpacityCo(float targetOpacity)
    {
        float duration = 0.2f;
        float startOpacity = isCurrentlyHovered? hoveredOpacity : idleOpacity;
        float progress = 0;

        while (progress < 1)
        {
            thumbCanvasGroup.alpha = Mathf.Lerp(startOpacity, targetOpacity, progress);
            progress += Time.deltaTime / duration;
            yield return null;
        }
        thumbCanvasGroup.alpha = targetOpacity;
    }
}
```

