#include <stdio.h>

// Function to allocate memory for a 2D array using mmap
int **allocate2DArray(int n) {
    int size = n * n * sizeof(int);
    int fd = shm_open("/2D_ARRAY", O_RDWR | O_CREAT, 0666);
    if (fd == -1) { // Check for file creation failure
        printf("Error creating shared memory file\n");
        return NULL;
    }

    // Truncate the file to the desired size
    if (ftruncate(fd, size) == -1) { // Check for file truncation failure
        printf("Error truncating shared memory file\n");
        close(fd);
        return NULL;
    }

    // Map the file to memory
    void *memory = mmap(NULL, size, PROT_READ | PROT_WRITE, MAP_SHARED, fd, 0);
    if (memory == MAP_FAILED) { // Check for memory mapping failure
        printf("Error mapping shared memory file to memory\n");
        close(fd);
        return NULL;
    }

    // Cast the mapped memory to a 2D array
    int **array = (int **)memory;
    for (int i = 0; i < n; i++) {
        array[i] = (int *)((char *)memory + i * n * sizeof(int));
    }

    close(fd);
    return array;
}

// Function to deallocate memory for the 2D array using shm_unmap and shm.detach
void deallocate2DArray(int **array, int n) {
    int fd = shm_open("/2D_ARRAY", O_RDWR);
    if (fd!= -1) {
        shm_unlink("/2D_ARRAY"); // Detach and unmap the shared memory file
    }
    close(fd);
}
```

