```javascript
import { NextApiRequest, NextApiResponse } from 'next';

const users = [];

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  switch (req.method) {
    case 'POST':
      const { username, password } = req.body;
      const existingUser = users.find((user) => user.username === username);
      if (existingUser) {
        res.status(403).json({ error: 'Username already taken' });
        return;
      }
      const hashedPassword = await bcrypt.hash(password, 10);
      users.push({ username, hashedPassword });
      res.status(201).json({ username });
      break;
    case 'GET':
      if (req.query.username && req.query.password) {
        const user = users.find((user) => user.username === req.query.username);
        if (user) {
          const isValidPassword = await bcrypt.compare(req.query.password, user.hashedPassword);
          if (isValidPassword) {
            return res.status(200).json({ username: user.username });
          } else {
            return res.status(401).json({ error: 'Invalid password' });
          }
        } else {
          return res.status(401).json({ error: 'Invalid username or password' });
        }
      } else {
        return res.status(401).json({ error: 'Missing username or password' });
      }
    default:
      return res.status(405).json({ error: 'Invalid method' });
  }
}
```

