using System;
using System.Windows;
using System.Windows.Data;
using System.Timers;

public class StopwatchViewModel
{
    private bool _running;
    private int seconds;
    private int minutes;
    private TimeSpan lapTime;
    private Timer timer;

    public Dashboard()
    {
        timer = new Timer(1000);
        timer.Elapsed += new ElapsedEventHandler(Timer_Elapsed);
        _running = false;
    }

    private void Timer_Elapsed(object sender, ElapsedEventArgs e)
    {
        if (_running)
        {
            seconds++;
            if (seconds == 60)
            {
                minutes++;
                seconds = 0;
            }
            Time = $"{minutes:00}:{seconds:00}";
            lapTime += TimeSpan.FromSeconds(1);
            LapTime = $"Lap Time: {lapTime:D}\\m\\sql";
        }
    }

    public string Time
    {
        get { return $"{minutes:00}:{seconds:00}"; }
    }
    public string LapTime
    {
        get { return $"Lap Time: {(lapTime:D):ctime0}"; }
    }

    public void Start()
    {
        _running = true;
        timer.Start();
    }

    public void Stop()
    {
        _running = false;
        timer.Stop();
    }

    public void Reset()
    {
        _running = false;
        timer.Stop();
        seconds = 0;
        minutes = 0;
        lapTime = TimeSpan.Zero;
        Time = $"{minutes:00}:{seconds:00}";
        LapTime = "Lap Time: 00:00:00";
    }
}

