```python
import json

def load_hierarchy(filename):
    """
    Loads a hierarchy from a JSON file.

    Args:
    filename (str): The name of the file to load the hierarchy from.

    Returns:
    dict: A nested dictionary representing the hierarchy.
    """
    try:
        with open(filename, 'r') as file:
            hierarchy = json.load(file)
            return hierarchy
    except FileNotFoundError:
        print(f"The file {filename} was not found.")
        return None
    except json.JSONDecodeError:
        print(f"Failed to parse the JSON in {filename}.")
        return None

def load_hierarchical_data(filename):
    """
    Loads a hierarchy and a set of hierarchical data from separate files.

    Args:
    filename (str): The name of the hierarchy file to load.

    Returns:
    tuple: A tuple containing the hierarchy and a list of hierarchical data.
    """
    hierarchy = load_hierarchy(f"{filename}.hierarchy")
    if hierarchy is None:
        return None, None
    data_filename = f"{filename}_data"
    try:
        with open(data_filename, 'r') as file:
            hierarchical_data = json.load(file)
            return hierarchy, hierarchical_data
    except FileNotFoundError:
        print(f"The file {data_filename} was not found.")
        return hierarchy, None
    except json.JSONDecodeError:
        print(f"Failed to parse the JSON in {data_filename}.")
