import pandas as pd
import numpy as np

def calculate_mismatches(df):
    # Calculate the average ratio for each column
    ratios = {}
    for col in df.columns:
        ratio = df[col].mean() / df[col].median()
        ratios[col] = ratio

    # Calculate the similarity score for each pair of columns
    similarity_scores = {}
    for col1 in df.columns:
        similarity_scores[col1] = {}
        for col2 in df.columns:
            if col1!= col2:
                difference = (np.abs(df[col1] - df[col2])).mean()
                similarity = 1 - difference / df[col1].max()
                similarity_scores[col1][col2] = similarity

    # Find the highest similarity score for each column
    highest_similarity_scores = {}
    most_different_columns = {}
    for col in df.columns:
        max_similarity_score = 0
        most_different_col = ''
        for col2 in df.columns:
            if col!= col2:
                similarity_score = similarity_scores[col][col2]
                if similarity_score > max_similarity_score:
                    max_similarity_score = similarity_score
                    most_different_col = col2
        highest_similarity_scores[col] = max_similarity_score
        most_different_columns[col] = most_different_col

    # Create the output DataFrame
    output_df = pd.DataFrame({
        'Column': df.columns,
        'Highest Similarity Score': highest_similarity_scores,
        'Column Most Different': most_different_columns
    })

    return output_df

# Example usage
