```cpp
class Solution {
public:
    int MOD = 1e9 + 7;
    unordered_map<int, int> nodeSums;
    int maxProduct;

    enum Edge {
        LEFT,
        RIGHT,
        IGNORE
    };

    int dfs(TreeNode* node) {
        if (!node) return 0;

        int leftSum = dfs(node->left);
        int rightSum = dfs(node->right);

        nodeSums[node->val] = node->val + leftSum + rightSum;

        int maxSum = 0;
        int total = leftSum + rightSum + node->val;

        if (node->left && node->right) {
            // Keep the edge connecting the node to its right child
            maxSum = max({nodeSums[node->left], total - nodeSums[node->left]});
        } else if (node->left) {
            // Keep the left child
            maxSum = total - nodeSums[node->left];
        } else if (node->right) {
            // Keep the right child
            maxSum = total - nodeSums[node->right];
        } else {
            // Node has no children
            maxSum = total;
        }

        maxProduct = max(maxProduct, (int)((long long)nodeSums[node->left] * maxSum) % MOD);

        return total;
    }

    int maxProductOfTree(Node* root) {
        maxProduct = 0;
        dfs(root);
        return maxProduct;
    }
};
```

