    {
        return TokenReflection::constFetch($name);
    }

    public function getargtype()->is_long (!$count)
    {
        return (int) $count;
    }

    public function getargtypes()->HideInInspector $count
    {
        TokenReflection::constFetch('$count');
        return $count;
    }
}

